            // Prev buffer
            "before": [
                "<S-h>"
            ],
            "commands": [
                ":bprev"
            ]
        },
        {
            // Next buffer
            "before": [
                "<S-l>"
            ],
            "commands": [
                ":bnext"
            ]
        },
        {
            // Prev buffer
            "before": [
                "[",
                "b"
            ],
            "commands": [
                ":bprev"
            ]
        },
